sn_imt_diagnosis_tracing_system.processing_script:
(function(diagnosticRequestGR, diagnosticEventGR, helper, props) {   

	/**      
	* diagnosticRequestGR - Diagnostic request (diagnostic_request) glide record object      
	* diagnosticEventGR - Diagnostic event (diagnostic_request) glide record object      
	* answer - Set the answer variable to comma separated user sys_ids.Those will be added to the impacted users list to the diagnostic request      
	* helper - utility to use methods like "addImpactedusers", "getProps" etc.Check "TracingSystemHelperUtil" script includes for more methods      
	* props - Access props by props.<name of the property> (Use suffix)      
	**/      
	//Get Impacted users
    var util =  new x_aleen_trace.AlertScheduledDiagnosticsUtil(diagnosticRequestGR, diagnosticEventGR);
	var impactedUsers = util.markAndGetImpactedUsers();

	//Set answer to comma separated user sys_ids
	answer = impactedUsers;

})(diagnosticRequestGR, diagnosticEventGR, helper, props);
